import React,{useState} from 'react';
import {View, Text,StyleSheet, TextInput,TouchableOpacity} from 'react-native';
import Title from './Title';
import Select from 'react-select';
import { SelectList } from 'react-native-dropdown-select-list'


const SetQuiz = ({navigation}:{navigation:any}) =>{
    const[ques, setQues] = useState('');
    const[option1,setOption1] = useState('');
    const[option2,setOption2] = useState('');
    const[option3,setOption3] = useState('');
    const[option4,setOption4] = useState('');
    const[selectValue,setSelectValue] = useState(null);
    const selectedCourse = [
        {value:"MATH", label:"Math"},
        {value:"SCIENCE", label:"Science"},
        {value:"HISTORY", label:"History"},
        {value:"ENGLISH", label:"English"}
    ]
    return (
        <View style={styles.container}>
            <Title/>
            <Text style={{fontSize:30, fontWeight:'600', marginBottom:20,color:'#fff'}}>New Question</Text>
            <View style={styles.modalView}>
                {/* <Select options={options} defaultValue={selectValue} 
                placeholder="Select Course" isSearchable/> */}
                 <SelectList boxStyles={{padding:10,width:250,borderColor:'skyblue',marginBottom:10}}
        setSelected={(val:any) => setSelectValue(val)} search={true}
        data={selectedCourse} maxHeight={50}
        save="value" placeholder='Select Course' />
                <TextInput style={styles.question} placeholder='Enter Question' 
                onChangeText={(ques)=>setQues(ques)}></TextInput>
                <TextInput style={styles.options} placeholder='Option1'
                onChangeText={(option1)=>setQues(option1)}></TextInput>
                <TextInput style={styles.options} placeholder='Option2'
                onChangeText={(option2)=>setQues(option2)}></TextInput>
                <TextInput style={styles.options} placeholder='Option3'
                onChangeText={(option3)=>setQues(option3)}></TextInput>
                <TextInput style={styles.options} placeholder='Option4'
                onChangeText={(option4)=>setQues(option4)}></TextInput>

                <TouchableOpacity style={styles.button} onPress={()=>navigation.navigate('Display')}>               
        <Text style={styles.buttonText}>Create</Text>
        </TouchableOpacity> 

            </View>

        </View>
    )
}
const styles = StyleSheet.create({
    container:{
        backgroundColor: '#00798c',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop:25,
        marginLeft:10,
        marginRight:10,
        borderRadius:10,
        height:'100%'
        
       },
       modalView:{
       marginBottom:50,
       justifyContent:'center',
       alignItems:'center', 
       width:300,
       height:450,
       backgroundColor:'white',
       borderRadius:20,
       shadowColor:'#bbb',
       shadowOpacity:0.80,
       elevation:5
   },
   selectOption:{
    borderWidth:1,
    borderColor:'skyblue',
    borderRadius:10,
    paddingHorizontal:10,
    padding:10,
    //paddingTop:10,
    width:250,
    marginBottom:10,
   },
   question:{
    borderWidth:1,
    borderColor:'skyblue',
    borderRadius:10,
    paddingHorizontal:10,
    padding:40,
    paddingTop:10,
    width:250,
    marginBottom:10,

   },
   options:{
    borderWidth:1,
    borderColor:'skyblue',
    borderRadius:10,
    paddingHorizontal:10,
    padding:10,
    paddingTop:10,
    width:250,
    marginBottom:10,
    
   },
   button:{
    width:150,
    height:50,
    backgroundColor:'#00b4d8',
    padding:10,
    borderRadius:15,
    marginTop:10
},
buttonText:{
    color:'#fff',
    fontSize:20,
    justifyContent:'center',
    alignItems:'center',
    textAlign:'center'
    
},
})
export default SetQuiz;
