import React, {useState} from "react";
import {View,Text,StyleSheet,TextInput,TouchableOpacity, Pressable, Platform} from 'react-native';
import Title from "./Title";
import DateTimePicker from '@react-native-community/datetimepicker';
const AnnouncementPage = ({navigation}:{navigation:any})=>{
    const[text,setText] = useState('');
    const[date,setDate] = useState('');

    //for date set
    const[newDate,setNewDate] = useState(new Date());
    const[showPicker,setShowPicker] = useState(false);
    const toggleDatePicker = ()=>{
        setShowPicker(!showPicker)
    }
    const onChange = ({ type }, selectedDate)=>{
        if (type == "set"){
            const currentDate = selectedDate;
            setNewDate(currentDate)
            if(Platform.OS === "ios"){
                toggleDatePicker();
                setDate(currentDate.toDateString());
            }
        }else{
            toggleDatePicker()
        }

    }
    return(
        <View style={styles.container}>
            <Title/>
            <Text style={{fontSize:30, fontWeight:'600', marginBottom:20}}>Announcement</Text>
                 <View style={styles.modalView}>
                 <TextInput style={styles.input1} placeholder="Enter Announcement" onChangeText={(text)=>setText(text)}></TextInput>
                {showPicker && (
                    <DateTimePicker
                    mode="date" 
                    display="spinner"
                    value={newDate}
                    onChange={onChange}/>
                )}
               {!showPicker && (
                 <Pressable onPress={toggleDatePicker}>
                 <TextInput style={styles.input2} 
                 placeholder="Enter Date" value={date} 
                 onChangeText={setDate} editable={false}>
                 </TextInput>
                 </Pressable>
               )}
                 <TouchableOpacity style={styles.button} onPress={()=>navigation.navigate('OptionPage')}>               
        <Text style={styles.buttonText}>Save</Text>
        </TouchableOpacity> 
                    </View>
                 </View>
    )
}
const styles = StyleSheet.create({
    container:{
     backgroundColor: '#00798c',
     alignItems: 'center',
     justifyContent: 'center',
     marginTop:25,
     marginLeft:10,
     marginRight:10,
     borderRadius:10,
     height:'100%'
     
    },
 modalView:{  
     marginBottom:180,
     justifyContent:'center',
     alignItems:'center', 
     width:300,
     height:300,
     backgroundColor:'white',
     borderRadius:20,
     shadowColor:'#bbb',
     shadowOpacity:0.80,
     elevation:5
 },
 input1:{
    borderWidth:1,
    borderColor:'skyblue',
    borderRadius:10,
    paddingHorizontal:10,
    padding:40,
    paddingTop:10,
    width:250,
    marginBottom:10,
},
input2:{
    borderWidth:1,
    borderColor:'skyblue',
    borderRadius:10,
    padding:10,
    width:250,
    marginBottom:10,
},
button:{
    width:150,
    height:50,
    backgroundColor:'#00b4d8',
    padding:10,
    borderRadius:15,
},
buttonText:{
    color:'#fff',
    fontSize:20,
    justifyContent:'center',
    alignItems:'center',
    textAlign:'center'
    
},
});
export default AnnouncementPage;
