import React, { useEffect, useState } from 'react';
import {View, Text, StyleSheet,TouchableOpacity} from 'react-native'
import Title from './Title';
import axios from 'axios';

const Profile = (userId:any)=>{
    const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://192.168.0.116:3000/users/${userId}`);
        setUserData(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, [userId]);
    return(
        <View style={styles.container}>
            <Title/>
            <Text style={{fontSize:40,color:'white'}}>Welcome to Setting Page</Text>
            <TouchableOpacity style={styles.button}>
                <Text style={styles.buttonText}> Visit Profile</Text>
            </TouchableOpacity>
        </View>
    )
}
const styles = StyleSheet.create({
    container:{
     backgroundColor: '#00798c',
     alignItems: 'center',
     justifyContent: 'center',
     marginTop:25,
     marginLeft:10,
     marginRight:10,
     borderRadius:10,
     height:'100%'
     
    },
    
    button:{
        width:300,
        backgroundColor:'#00b4d8',
        padding:10,
        borderRadius:25,
        marginBottom:40
    },
    buttonText:{
        color:'#fff',
        fontSize:30,
        justifyContent:'center',
        alignItems:'center',
        marginLeft:75
        
    }
})
export default Profile;
