import React from 'react';
import {View, Text,StyleSheet, TouchableOpacity} from 'react-native';
import Title from './Title';

const DisplayQuestion = ({navigation}:{navigation:any})=>{
    return(
        <View style={styles.container}>
        <Title/>
        <Text style={{fontSize:30, fontWeight:'600', marginBottom:20,color:'white'}}>History Question</Text>
        <View style={styles.modalView}>
            <Text>Q1. Who was the first President of the United States?</Text>
            <Text style={styles.option}> a. Benjamin Franklin</Text>
            <Text> b. Benjamin Franklin</Text>
            <Text> c. Benjamin Franklin</Text>
            <Text> d. Benjamin Franklin</Text>
        <TouchableOpacity style={styles.button} onPress={()=>navigation.navigate('OptionPage')}>               
        <Text style={styles.buttonText}>Save</Text>
        </TouchableOpacity> 

        </View>
        </View>
    )
}
const styles = StyleSheet.create({
    container:{
        backgroundColor: '#00798c',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop:25,
        marginLeft:10,
        marginRight:10,
        borderRadius:10,
        height:'100%'
        
       },
       modalView:{
       marginBottom:50,
       justifyContent:'center',
       alignItems:'center', 
       width:300,
       height:500,
       backgroundColor:'white',
       borderRadius:20,
       shadowColor:'#bbb',
       shadowOpacity:0.80,
       elevation:5
   },
   option:{
    marginRight:60
   },
   button:{
    width:150,
    height:50,
    backgroundColor:'#00b4d8',
    padding:10,
    borderRadius:15,
    marginTop:10
},
buttonText:{
    color:'#fff',
    fontSize:20,
    justifyContent:'center',
    alignItems:'center',
    textAlign:'center'
    
},
})
export default DisplayQuestion;
