import React, { useEffect, useState } from 'react';
import {View, Text, StyleSheet,TouchableOpacity,Image} from 'react-native'
import Title from './Title';
import axios from 'axios';

const Profile = (email:any)=>{
    const [userData, setUserData] = useState<any>({});

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://192.168.0.116:3000/users/${email}`);
        setUserData(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, [email]);
    return(
        <View style={styles.container}>
            <Title/>
            {userData ? (
        <View style={{marginBottom:60}}>
            <Text style={{fontSize:30,fontWeight:'500',color:"white", marginBottom:160}}>User Profile</Text>
             <Image source={{uri:"http://www.emmegi.co.uk/wp-content/uploads/2019/01/User-Icon.jpg"}} style={{width:150,height:150,borderRadius:80,marginBottom:80}}/>
          <View style={{marginBottom:50}}>
          <Text style={{fontSize:20,color:'white',fontWeight:'400'}}>First Name: {userData.first_name}</Text>
          <Text style={{fontSize:20,color:'white',fontWeight:'400'}}>Last Name: {userData.last_name}</Text>
          <Text >Email: {userData.email}</Text>
          </View>
          {/* Display other user information */}
        </View>
      ) : (
        <Text>Loading user data...</Text>
      )}
        </View>
    )
}
const styles = StyleSheet.create({
    container:{
     backgroundColor: '#00798c',
     alignItems: 'center',
     justifyContent: 'center',
     marginTop:25,
     marginLeft:10,
     marginRight:10,
     borderRadius:10,
     height:'100%'
     
    },
    
    button:{
        width:300,
        backgroundColor:'#00b4d8',
        padding:10,
        borderRadius:25,
        marginBottom:40
    },
    buttonText:{
        color:'#fff',
        fontSize:30,
        justifyContent:'center',
        alignItems:'center',
        marginLeft:75
        
    }
})
export default Profile;
