import {View, Text, StyleSheet,TouchableOpacity} from 'react-native';
import Title from './Title';
import { useEffect, useState } from 'react';

const shuffleArray=({array}:{array:any})=> {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
    }
}
const AttemptQuiz = (props:any) =>{
    const [questions, setQuestions] = useState<Question[]>([]);
  const [quesNo, setQuesNo] = useState(0);
  const [options, setOptions] = useState<string[]>([]);
  const[score,setScore] = useState(0);
    const getQuiestion =async () => {
        const url = "https://opentdb.com/api.php?amount=5&type=multiple&encode=url3986";
        const response = await fetch(url);
        const data = await response.json();
        setQuestions(data.results)
        setOptions(generateOptionsAndShuffle(data.results[0]));
    }
    useEffect(()=>{
        getQuiestion()
    },[])
    const handleNextPress = ()=>{
        setQuesNo(quesNo+1)
        setOptions(generateOptionsAndShuffle(questions[quesNo+1]))
    }
    interface Question {
        question: string;
        incorrect_answers: string[];
        correct_answer: string;
      }
      const generateOptionsAndShuffle = (question: Question): string[] => {
        if (!question) return [];
        const newOptions = [...question.incorrect_answers, question.correct_answer];
        shuffleArray({array:newOptions}); // Assuming shuffleArray mutates the array in place
        return newOptions;
      };
    
      const handleOptionSelect = (selectedOption: string) => {
        const correctAnswer = questions[quesNo].correct_answer;
        if (selectedOption === correctAnswer) {
          // Logic for correct answer selected
          //console.warn('Correct answer!');
            setScore(score+1);
          // You might want to update score or perform other actions
        } 
        if(quesNo!==4){
            setQuesNo(quesNo+1)
            setOptions(generateOptionsAndShuffle(questions[quesNo+1]))
        }
       
    }
    const showResults = ()=>{
        props.navigation.navigate('Result',{score:score})
    }

    return (
        <View style={styles.container}>
        <Title/>
        <Text style={{fontSize:30, fontWeight:'600', marginBottom:20,color:'white'}}>Questions</Text>
       {questions && quesNo < questions.length && (
       <View style={styles.modalView}>
           <Text style={{fontSize:18,marginTop:15, marginLeft:10}}>Q{quesNo}. {decodeURIComponent(questions[quesNo].question)}</Text>
           {options.length > 0 && options.map((option, index) => (
           <><TouchableOpacity style={styles.options} key={index} onPress={()=>handleOptionSelect(option)}>
                   <Text style={styles.optionText}>{decodeURIComponent(option)}</Text>
               </TouchableOpacity>
                   </>
           ))}
           <View style={styles.bottom}>
           <TouchableOpacity style={styles.button1}>               
            <Text style={styles.buttonText}>Skip</Text> 
         </TouchableOpacity>
         {quesNo !== 4 && 
          <TouchableOpacity style={styles.button2} onPress={handleNextPress}>               
          <Text style={styles.buttonText}>Next</Text>
       </TouchableOpacity>
       }
        {quesNo === 4 && 
          <TouchableOpacity style={styles.button2} onPress={showResults}>               
          <Text style={styles.buttonText}>Show Result</Text>
       </TouchableOpacity>
       }
        
         </View>
            </View>
       )
}
            </View>
    )
}
const styles = StyleSheet.create({
    container:{
        backgroundColor: '#00798c',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop:25,
        marginLeft:10,
        marginRight:10,
        borderRadius:10,
        height:'100%'
        
       },
       modalView:{
       marginBottom:20,
       justifyContent:'center',
       alignItems:'center', 
       width:320,
       height:400,
       backgroundColor:'white',
       borderRadius:20,
       shadowColor:'#bbb',
       shadowOpacity:0.80,
       elevation:5
   },
   options:{
    padding:10,
    //paddingHorizontal:100,
    width:'85%',
    backgroundColor:"#DFE9F1",
    borderRadius:10,
    marginTop:10
   },
   optionText:{
    fontSize:15,
    color:'black'
   },
   bottom:{
    marginTop:20,
    paddingVertical:20,
    flexDirection:'row',
    justifyContent:'space-between',
   },
   button1:{
    //position:'relative',
    backgroundColor:'#00b4d8',
    padding:10,
    paddingHorizontal:25,
    height:40,
    borderRadius:15,
    marginHorizontal:50
},
button2:{
    position:'relative',
    backgroundColor:'#00b4d8',
    padding:10,
    paddingHorizontal:25,
    borderRadius:15,
    marginHorizontal:50
},
buttonText:{
    color:'#fff',
    fontSize:15,
    justifyContent:'center',
    alignItems:'center',
    textAlign:'center'
    
},
})
export default AttemptQuiz;
