import React, { useState } from 'react';
import {View,Text,StyleSheet,TouchableOpacity, FlatList} from 'react-native';
import Title from './Title';
const coursesData = [
    { id: 1, title: 'Math', details: 'Details about Mathe Quiz' },
    { id: 2, title: 'Science', details: 'Details about Science Quiz' },
    { id: 3, title: 'History', details: 'Details about History Quiz' },
    // Add more courses as needed
  ];

const ViewAnnouncement = ({navigation}:{navigation:any})=>{
    const renderItem = ({ item }:{item:any}) => {
        return (
          <TouchableOpacity
            style={styles.itemContainer}
            onPress={() => navigation.navigate('CourseDetails', { details: item.details })}
          >
            <Text style={styles.itemText}>{item.title}</Text>
            <Text style={styles.viewDetailsText}>View Details</Text>
          </TouchableOpacity>
        );
      };
    return (
        
        <View style={styles.container}>
        <Title/>
        <Text style={{fontSize:30, fontWeight:'600', marginBottom:20,color:'white'}}>Announcements</Text>
        <View style={styles.modalView}>
            <FlatList data={coursesData} renderItem={renderItem} 
            keyExtractor={(item) => item.id.toString()}/>
            </View>
            
           
            <TouchableOpacity style={styles.button} onPress={()=>navigation.navigate('Home')}>                 
        <Text style={styles.buttonText}>Back</Text>
        
     </TouchableOpacity>
            </View>
    )
}
const styles = StyleSheet.create({
    container:{
        backgroundColor: '#00798c',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop:25,
        marginLeft:10,
        marginRight:10,
        borderRadius:10,
        height:'100%'
        
       },
       modalView:{
       marginBottom:20,
       justifyContent:'center',
       alignItems:'center', 
       width:300,
       height:400,
       backgroundColor:'white',
       borderRadius:20,
       shadowColor:'#bbb',
       shadowOpacity:0.80,
       elevation:5
   },
   itemContainer: {
    padding: 10,
    margin:10,
    flexDirection:"row",
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
  },
  itemText: {
    fontSize: 18,
    fontWeight: 'bold',
    marginRight:50
  },
  viewDetailsText: {
    color: 'blue',
    marginLeft:60,
    justifyContent:'space-between',
    textAlign:'left'
  },
   button:{
    width:150,
    height:50,
    backgroundColor:'#ccc',
    padding:10,
    borderRadius:15,
    marginBottom:50
},
buttonText:{
    color:'black',
    fontSize:20,
    justifyContent:'center',
    alignItems:'center',
    textAlign:'center'
    
},
})
export default ViewAnnouncement;
