import React, { useEffect, useState } from 'react';
import {View,Text,StyleSheet,TouchableOpacity, FlatList} from 'react-native';
import Title from './Title';
import axios from 'axios';

const ViewAnnouncement = ({navigation}:{navigation:any})=>{
  const [announcements, setAnnouncements] = useState<any[]>([]);

  useEffect(() => {
    async function fetchAnnouncement() {
      try {
        const response = await axios.get('http://192.168.0.116:3000/announcements');
        setAnnouncements(response.data);
        console.warn("Announcement displayed!")
      } catch (error) {
        console.error('Error fetching questions:', error);
      }
    }

    fetchAnnouncement();
  }, []);
      
    return (
        
        <View style={styles.container}>
        <Title/>
        <Text style={{fontSize:30, fontWeight:'600', marginBottom:20,color:'white'}}>Announcements</Text>
        <View style={styles.modalView}>
        <View style={styles.contentContainer}>
        {announcements.map((announcement:any) => (   
          <View key={announcement.id} style={styles.itemContainer}>
              <Text style={styles.itemText}>{announcement.id}.</Text>
            <Text style={styles.itemText}>
            {`${announcement.courseTitle}`}: </Text>
            <TouchableOpacity onPress={()=>navigation.navigate('CourseDetails',{ announcementId: announcement.id })} >
            <Text style={styles.viewDetailsText}>View details</Text> 
            </TouchableOpacity>
          </View>
         
        ))}
         </View>
            
           
            <TouchableOpacity style={styles.button} onPress={()=>navigation.navigate('OptionPage')}>                 
        <Text style={styles.buttonText}>Back</Text>
        
     </TouchableOpacity>
            </View>
            </View>
    )
}
const styles = StyleSheet.create({
    container:{
        backgroundColor: '#00798c',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop:25,
        marginLeft:10,
        marginRight:10,
        borderRadius:10,
        height:'100%'
        
       },
       modalView:{
       marginBottom:20,
       justifyContent:'center',
       alignItems:'center', 
       width:300,
       height:400,
       backgroundColor:'white',
       borderRadius:20,
       shadowColor:'#bbb',
       shadowOpacity:0.80,
       elevation:5
   },
   contentContainer: {
    flexGrow:1,
    flexDirection: 'column', // Align items horizontally
    alignItems: 'center',
    paddingHorizontal: 10,
    marginTop:10,
    marginLeft:10,
    marginRight:10,
  // Add some horizontal padding between items
  },
   itemContainer: {
    marginTop:10,
    marginLeft:10,
    marginRight:10,
    padding: 20, 
    //marginHorizontal:5,
    flexDirection:"row",
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
  },
  itemText: {
    fontSize: 18,
    fontWeight: 'bold',
    marginRight:10
  },
  viewDetailsText: {
    color: 'blue',
    marginLeft:60,
    marginRight:10,
    justifyContent:'space-between',
    textAlign:'left'
  },
   button:{
    width:150,
    height:50,
    backgroundColor:'#ccc',
    padding:10,
    borderRadius:15,
    marginBottom:50
},
buttonText:{
    color:'black',
    fontSize:20,
    justifyContent:'center',
    alignItems:'center',
    textAlign:'center'
    
},
})
export default ViewAnnouncement;
