import {View,Text, StyleSheet,TouchableOpacity} from 'react-native';
import Title from './Title';
import ViewAnnouncement from './ViewAnnouncement';

const ViewDetails = (props:any) => {
    const { details } = props.route.params;
    return (
      <View style={styles.container}>
        <Title/>
        <Text style={{fontSize:30, fontWeight:'600', marginBottom:20,color:'white'}}>Details</Text>
        <View style={styles.modalView}>
        <View style={styles.detailContainer}>
        <Text style={styles.label}>Topic:</Text>
        <Text style={styles.value}>{details.topics}</Text>
      </View>
        <View style={styles.detailContainer}>
        <Text style={styles.label}>Date:</Text>
        <Text style={styles.value}>{details.date}</Text>
      </View>
      <View style={styles.detailContainer}>
        <Text style={styles.label}>Time:</Text>
        <Text style={styles.value}>{details.time}</Text>
      </View>
      <View style={styles.detailContainer}>
        <Text style={styles.label}>Duration:</Text>
        <Text style={styles.value}>{details.duration}</Text>
      </View>
        </View>
        <TouchableOpacity style={styles.button} onPress={()=>props.navigation.navigate('OptionPage')}>                 
        <Text style={styles.buttonText}>Back</Text>
        
     </TouchableOpacity>
      </View>
    );
  };
  const styles = StyleSheet.create({
    container:{
        backgroundColor: '#00798c',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop:25,
        marginLeft:10,
        marginRight:10,
        borderRadius:10,
        height:'100%'
        
       },
       modalView:{
       marginBottom:20,
       justifyContent:'center',
       alignItems:'center', 
       width:300,
       height:200,
       backgroundColor:'white',
       borderRadius:20,
       shadowColor:'#bbb',
       shadowOpacity:0.80,
       elevation:5
   },
   detailContainer: {
    flexDirection: 'row',
    alignItems:'center',
   //marginEnd:80,
    marginBottom: 10,

  },
  label: {
    fontWeight: 'bold',
    marginRight: 10,
    fontSize:20,
    textAlign: 'left',


  },
  value: {
    fontSize: 20,
  },
   button:{
    width:150,
    height:50,
    backgroundColor:'#ccc',
    padding:10,
    borderRadius:15,
    marginBottom:50
},
buttonText:{
    color:'black',
    fontSize:20,
    justifyContent:'center',
    alignItems:'center',
    textAlign:'center'
    
},
})
export default ViewDetails;
