import {View,Text, StyleSheet,TouchableOpacity} from 'react-native';
import Title from './Title';
import ViewAnnouncement from './ViewAnnouncement';
import { useEffect, useState } from 'react';
import axios from 'axios';

const ViewDetails = (props:any) => {
  const [details, setDetails] = useState<any[]>([]);
  const { detail } = props.route.params;

  useEffect(() => {
    async function fetchDetails() {
      try {
        //const id = parseInt(props.route.params.id); 
        // if (!isNaN(id)) {
          const response = await axios.get('http://192.168.0.116:3000/announcements/${id}');
        setDetails(response.data);
        console.warn("Announcement details displayed!")
      } catch (error) {
        console.error('Error fetching questions:', error);
      }
    }

    fetchDetails();
  }, [detail]);
      
    return (
      <View style={styles.container}>
        <Title/>
        <Text style={{fontSize:30, fontWeight:'600', marginBottom:20,color:'white'}}>Details</Text>
        <View style={styles.modalView}>
        <View style={styles.detailContainer}>
        {/* <Text style={styles.label}>Topic:</Text> */}
        {/* {details.length > 0 ? (details.map((detail:any) => (   
          <View key={detail.id}>
              <Text style={styles.value}>{`${detail.courseTitle}`}.</Text>
            <Text style={styles.value}>
            {`${detail.announcement}`}: </Text>
            <Text style={styles.value}>
            {`${detail.date}`}: </Text>
      </View>):(
        <Text>No details Availabel</Text>
      )
       )} */}
       {details ? (
        details.map((detail: any) => (
          <View key={detail.id}>
            <Text style={styles.value}>{`${detail.courseTitle}`}.</Text>
            <Text style={styles.value}>
            {`${detail.announcement}`}: </Text>
            <Text style={styles.value}>
            {`${detail.date}`}: </Text>
          </View>
        ))
      ) : (
        <Text>No details available</Text>
      )}
    </View>
       
      </View>
      <TouchableOpacity style={styles.button} onPress={()=>props.navigation.navigate('OptionPage')}>                 
        <Text style={styles.buttonText}>Back</Text>
        
     </TouchableOpacity>
      </View>
    );
  };
  const styles = StyleSheet.create({
    container:{
        backgroundColor: '#00798c',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop:25,
        marginLeft:10,
        marginRight:10,
        borderRadius:10,
        height:'100%'
        
       },
       modalView:{
       marginBottom:20,
       justifyContent:'center',
       alignItems:'center', 
       width:300,
       height:200,
       backgroundColor:'white',
       borderRadius:20,
       shadowColor:'#bbb',
       shadowOpacity:0.80,
       elevation:5
   },
   detailContainer: {
    flexDirection: 'row',
    alignItems:'center',
   //marginEnd:80,
    marginBottom: 10,

  },
  label: {
    fontWeight: 'bold',
    marginRight: 10,
    fontSize:20,
    textAlign: 'left',


  },
  value: {
    fontSize: 20,
  },
   button:{
    width:150,
    height:50,
    backgroundColor:'#ccc',
    padding:10,
    borderRadius:15,
    marginBottom:50
},
buttonText:{
    color:'black',
    fontSize:20,
    justifyContent:'center',
    alignItems:'center',
    textAlign:'center'
    
},
})
export default ViewDetails;
