import React, { useState } from 'react';
import {View,Text,StyleSheet,TouchableOpacity, FlatList} from 'react-native';
import Title from './Title';
const coursesData = [
    { id: 1, title: 'Math', details: 'Details about Mathe Quiz' },
    { id: 2, title: 'Science', details: 'Details about Science Quiz' },
    { id: 3, title: 'History', details: 'Details about History Quiz' },
    // Add more courses as needed
  ];
  const [course,setCourse] = useState([])

const ViewAnnouncement = ({navigation}:{navigation:any})=>{
    return (
        <View style={styles.container}>
        <Title/>
        <Text style={{fontSize:30, fontWeight:'600', marginBottom:20,color:'white'}}>Announcements</Text>
        <View style={styles.modalView}>
            {coursesData.length?
            <FlatList data={course} renderItem={({item})=>
        <View>
            <Text>{item.id}</Text>
            </View>}/>:null}
            </View>
            
           
            <TouchableOpacity style={styles.button} onPress={()=>navigation.navigate('Home')}>                 
        <Text style={styles.buttonText}>Back</Text>
        
     </TouchableOpacity>
            </View>
    )
}
const styles = StyleSheet.create({
    container:{
        backgroundColor: '#00798c',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop:25,
        marginLeft:10,
        marginRight:10,
        borderRadius:10,
        height:'100%'
        
       },
       modalView:{
       marginBottom:20,
       justifyContent:'center',
       alignItems:'center', 
       width:300,
       height:400,
       backgroundColor:'white',
       borderRadius:20,
       shadowColor:'#bbb',
       shadowOpacity:0.80,
       elevation:5
   },
   text:{
    fontSize:20,
    color:'#096BB3',
    marginEnd:20
    
   },
   button:{
    width:150,
    height:50,
    backgroundColor:'#ccc',
    padding:10,
    borderRadius:15,
    marginBottom:50
},
buttonText:{
    color:'black',
    fontSize:20,
    justifyContent:'center',
    alignItems:'center',
    textAlign:'center'
    
},
})
export default ViewAnnouncement;
